css:
atom-notifications:empty {
  pointer-events: none;
}
atom-text-editor::shadow atom-text-editor-minimap,
atom-text-editor atom-text-editor-minimap {
  display: block;
  width: 10%;
  height: 100%;
  overflow: hidden;
  position: relative;
  -webkit-user-select: none;
}
atom-text-editor::shadow atom-text-editor-minimap::shadow canvas,
atom-text-editor atom-text-editor-minimap::shadow canvas {
  position: absolute;
  top: 0;
  left: 0;
  transform-origin: 0 0;
}
atom-text-editor::shadow atom-text-editor-minimap::shadow .minimap-visible-area,
atom-text-editor atom-text-editor-minimap::shadow .minimap-visible-area {
  position: absolute;
  display: 'block';
  background-color: #888;
  opacity: .2;
  cursor: -webkit-grab;
}
atom-text-editor::shadow atom-text-editor-minimap::shadow .minimap-controls,
atom-text-editor atom-text-editor-minimap::shadow .minimap-controls {
  position: relative;
  height: 100%;
  pointer-events: none;
}
atom-text-editor::shadow atom-text-editor-minimap::shadow .minimap-scroll-indicator,
atom-text-editor atom-text-editor-minimap::shadow .minimap-scroll-indicator {
  position: absolute;
  display: block;
  right: 0;
  width: 2px;
  min-height: 2px;
  z-index: 10;
  background: #4182c4;
}
atom-text-editor::shadow atom-text-editor-minimap::shadow .open-minimap-quick-settings,
atom-text-editor atom-text-editor-minimap::shadow .open-minimap-quick-settings {
  opacity: 0;
  position: absolute;
  z-index: 1;
  cursor: pointer;
  pointer-events: auto;
  color: #aaaaaa;
  font-size: 20px;
  text-align: center;
  line-height: 24px;
  width: 24px;
  height: 24px;
  display: block;
  right: 4px;
  transition: opacity 0.4s;
}
atom-text-editor::shadow atom-text-editor-minimap::shadow .open-minimap-quick-settings:before,
atom-text-editor atom-text-editor-minimap::shadow .open-minimap-quick-settings:before {
  content: '\f02f';
  font-family: 'Octicons Regular';
}
atom-text-editor::shadow atom-text-editor-minimap:hover::shadow .open-minimap-quick-settings,
atom-text-editor atom-text-editor-minimap:hover::shadow .open-minimap-quick-settings {
  opacity: 1;
  transition: opacity 0.1s;
}
atom-pane.with-minimap atom-text-editor:not(.minimap-editor):not(.mini),
atom-pane.with-minimap :host:not(.minimap-editor):not(.mini),
atom-pane.with-minimap atom-text-editor:not(.minimap-editor):not(.mini),
atom-pane.with-minimap atom-text-editor::shadow:not(.minimap-editor):not(.mini) {
  padding-right: 10%;
}
atom-pane.with-minimap atom-text-editor .vertical-scrollbar,
atom-pane.with-minimap :host .vertical-scrollbar,
atom-pane.with-minimap atom-text-editor .vertical-scrollbar,
atom-pane.with-minimap atom-text-editor::shadow .vertical-scrollbar {
  right: 0 !important;
}
.zen atom-pane.with-minimap atom-text-editor,
.zen atom-pane.with-minimap atom-text-editor::shadow {
  padding-right: 0 !important;
}
.zen atom-pane.with-minimap atom-text-editor .vertical-scrollbar,
.zen atom-pane.with-minimap atom-text-editor::shadow .vertical-scrollbar {
  right: 0 !important;
}
.minimap-quick-settings {
  position: absolute !important;
}
.minimap-quick-settings .hidden-input {
  position: absolute;
  width: 0;
  height: 0;
  background: transparent;
  border: none;
}
.minimap-quick-settings ol {
  margin-top: 0 !important;
}
.minimap-quick-settings ol .separator {
  background: rgba(255, 255, 255, 0.07);
  height: 1px;
}
.minimap-quick-settings ol .separator:first-child {
  display: none;
}
.minimap-quick-settings ol li:hover {
  background: rgba(255, 255, 255, 0.07);
}
.open-minimap-quick-settings {
  opacity: 0;
  position: absolute;
  z-index: 1;
  cursor: pointer;
  color: #aaaaaa;
  font-size: 20px;
  text-align: center;
  line-height: 24px;
  width: 24px;
  height: 24px;
  display: block;
  right: 4px;
  transition: opacity 0.4s;
}
.open-minimap-quick-settings:before {
  content: '\f02f';
  font-family: 'Octicons Regular';
}
.minimap:hover .open-minimap-quick-settings {
  opacity: 1;
  transition: opacity 0.1s;
}
.minimap-on-left atom-pane.with-minimap atom-text-editor:not(.minimap-editor):not(.mini),
.minimap-on-left atom-pane.with-minimap :host:not(.minimap-editor):not(.mini),
.minimap-on-left atom-pane.with-minimap atom-text-editor:not(.minimap-editor):not(.mini),
.minimap-on-left atom-pane.with-minimap atom-text-editor::shadow:not(.minimap-editor):not(.mini) {
  padding-right: 0;
  padding-left: 10%;
}
.minimap-on-left atom-pane.with-minimap .vertical-scrollbar {
  right: 0;
}
.minimap-on-left atom-pane.with-minimap .minimap {
  left: 0 !important;
}
.minimap {
  position: absolute;
  right: 0;
  bottom: 0;
  left: initial !important;
  width: 10%;
  overflow: hidden;
  box-sizing: border-box;
  -webkit-user-select: none;
}
.minimap atom-text-editor {
  padding: 0 !important;
  background: transparent;
}
.minimap .minimap-scroller {
  position: absolute;
  display: none;
  right: 0;
  width: 2px;
  min-height: 2px;
  z-index: 10;
  background: #4182c4;
}
.minimap .minimap-scroller.visible {
  display: block;
}
.minimap .minimap-wrapper {
  position: absolute;
  width: 100%;
  height: 100%;
  -webkit-transform-origin: 0 0;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  -webkit-transform: translateZ(0);
  transform: translateZ(0);
}
.minimap .minimap-underlayer,
.minimap .minimap-overlayer {
  -webkit-transform-origin: 0 0;
}
.minimap .minimap-visible-area {
  position: absolute;
  width: 100%;
  background-color: #888;
  opacity: .2;
  cursor: -webkit-grab;
}
.minimap .minimap-visible-area:active {
  cursor: -webkit-grabbing;
}
.minimap .minimap-editor {
  position: absolute;
  cursor: default;
  height: 100%;
  width: 100%;
}
.minimap .minimap-canvas {
  width: 100%;
  height: 100%;
}
.minimap .accelerated {
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  -webkit-transform: translateZ(0);
  transform: translateZ(0);
}
